pragma solidity ^0.5.0;

contract Homegrow {
    //based on subcurrency example from solidity docs --> since we intend to have this be local currency basically 
    address public minter; 
    mapping (address => uint) public balances; 

    event Sent(address from, address to, uint amount); 

    //this is the constructor whose code is only run when the contract is created
    constructor() public {
        minter = msg.sender; 
    }

    function mint(address receiver, uint amount) public {
        if (msg.sender != minter) return; 
        balances[receiver] += amount; 
    }

    function sendMoney(address sender, address receiver, uint amount) public {
        // if (balances[msg.sender] < amount) return; 
        balances[sender] -= 10;
        balances[receiver] += 10; 
        emit Sent(sender, receiver, amount); 
    }

    function getBalance(address balancee) public returns (uint) {
        return balances[balancee];
    }

}
